mapper.update:
update t_stu
set stu_name = #{stuName,jdbcType=VARCHAR},
  stu_password = #{stuPassword,jdbcType=VARCHAR},
  suid = #{suid,jdbcType=INTEGER}
where stu_id = #{stuId,jdbcType=INTEGER}

mapper.select:
select stu_id, stu_name, stu_password, suid
from t_stu
where stu_id = #{stuId,jdbcType=INTEGER}
